(function() {
  var cutsTheMustard = function() {
    return 'addEventListener' in window && 'querySelector' in document && 'localStorage' in window;
  };

  if (!cutsTheMustard()) {
    return;
  }

  var chart = document.getElementById('nw-us2020-banner__chart');
  var resultsBar = document.getElementById('nw-us2020-banner__bar');
  var countDem = document.getElementById('nw-us2020-banner__votes-count--dem');
  var countGop = document.getElementById('nw-us2020-banner__votes-count--gop');
  var barDem = document.getElementById('nw-us2020-banner__votes-bar--dem');
  var barGop = document.getElementById('nw-us2020-banner__votes-bar--gop');

  var insideClass = 'nw-us2020-banner__votes-count--inside';
  var outsideClass = 'nw-us2020-banner__votes-count--outside';
  var underClass = 'nw-us2020-banner__chart--under';

  var PLACEMENT = {
    INSIDE: 'IN',
    OUTSIDE: 'OUT',
    UNDER: 'UNDER'
  };

  var placeLabel = function(label, placement) {
    if (placement === PLACEMENT.INSIDE) {
      label.classList.add(insideClass);
      label.classList.remove(outsideClass);
      return;
    }

    if (placement === PLACEMENT.OUTSIDE) {
      label.classList.remove(insideClass);
      label.classList.add(outsideClass);
      return;
    }

    label.classList.remove(insideClass);
    label.classList.remove(outsideClass);
  };

  var getLabelPlacement = function(label, bar, otherBar, resultWidth) {
    const labelWidth = label.offsetWidth;
    const barWidth = bar.clientWidth;
    const otherBarWidth = otherBar.clientWidth;
    const halfResultWidth = (resultWidth - 1) / 2;

    // spaceAvailable is space between color bar and center OR other party's bar. Can be negative.
    const spaceAvailable = Math.min(halfResultWidth - barWidth, resultWidth - otherBarWidth - barWidth);

    if (barWidth >= labelWidth) {
      return PLACEMENT.INSIDE;
    }

    if (labelWidth > spaceAvailable) {
      return PLACEMENT.UNDER;
    }

    return PLACEMENT.OUTSIDE;
  };

  var positionLabels = function() {
    const resultWidth = resultsBar.clientWidth;

    const labelPlacementDem = getLabelPlacement(countDem, barDem, barGop, resultWidth);
