      placeLabel(countDem, PLACEMENT.UNDER);
      placeLabel(countGop, PLACEMENT.UNDER);
      return;
    }

    chart.classList.remove(underClass);
    placeLabel(countDem, labelPlacementDem);
    placeLabel(countGop, labelPlacementGop);
  };

